import { mapMutations, mapState } from 'vuex';
export default {
  name: 'App',
  store,
  data() {
    return {
      center: {
        lat: 43.65,
        lng: -79.38
      },
      currentPlace: null,
      markers: [],
      places: []
    };
  },
  computed: {
    ...mapState(['storeC', 'storeM', 'storeP'])
  },
  methods: {
    setPlace(place) {
      this.currentPlace = place;
    },
    addMarker() {
      if (this.currentPlace) {
        const marker = {
          lat: this.currentPlace.geometry.location.lat(),
          lng: this.currentPlace.geometry.location.lng()
        };
        store.commit('addMarkers', marker);
        store.commit('addPlaces', this.currentPlace);
        store.commit('changeCenter', marker);
        // this.markers.push({ position: marker });
        // this.places.push(this.currentPlace);
        // this.center = marker;
        this.currentPlace = null;
      }
    },
    geolocate: function () {
      navigator.geolocation.getCurrentPosition(position => {
        const current = {
          lat: position.coords.latitude,
          lng: position.coords.longitude
        };
        store.commit('addMarkers', current);
        store.commit('changeCenter', current);
        // this.markers.push({position:current});
        // this.center = current;
      });
    }
  }
};
